    'diamond_earring' => $base_path . 'diamond/diamond_earring_product.php',
    'diamond_necklaces' => $base_path . 'diamond/diamond_necklaces_product.php',
    'diamond_pendants' => $base_path . 'diamond/diamond_pendants_product.php',
    // Gold
